import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'estacionamento';
  owner = '';

  convertPeriod(mil: number): string {
    const min = Math.floor(mil / 60000);
    const sec = Math.floor((mil % 60000) / 1000);
    return `${min}m e ${sec}s`;
  }

  renderGarage(): void {
    const garage = this.getGarage();
    const garageElement = document.querySelector("#garage");
    if (garageElement) {
      garageElement.innerHTML = "";
      garage.forEach(c => this.addCarToGarage(c));
    }
  }

  addCarToGarage(car: { name: string, licence: string, time: Date }): void {
    const row = document.createElement("tr");
    row.innerHTML = `
      <td>${car.name}</td>
      <td>${car.licence}</td>
      <td data-time="${car.time}">
        ${car.time.toLocaleString('pt-BR', {
          hour: 'numeric', minute: 'numeric'
        })}
      </td>
      <td>
        <button class="delete">x</button>
      </td>
    `;
    const garageElement = document.querySelector("#garage");
    if (garageElement) {
      garageElement.appendChild(row);
    }
  }

  checkOut(info: HTMLTableDataCellElement[]): void {
    const period: number = Date.now() - new Date(info[2].dataset['time']!).getTime();
    const formattedPeriod: string = this.convertPeriod(period);
    const licence: string = info[1].textContent!;
    const msg: string = `O veículo ${info[0].textContent} de placa ${licence} permaneceu ${formattedPeriod} estacionado. \n\n Deseja encerrar?`;
    if (!confirm(msg)) return;
    const garage: { licence: string }[] = this.getGarage().filter(c => c.licence !== licence);
    localStorage.setItem('garage', JSON.stringify(garage));
    this.renderGarage();
  }

  getGarage(): { name: string, licence: string, time: Date }[] {
    const garageData = localStorage.getItem('garage');
    return garageData ? JSON.parse(garageData) : [];
  }

  onClickSend(): void {
    const name = (<HTMLInputElement>document.querySelector("#name"))?.value;
    const licence = (<HTMLInputElement>document.querySelector("#licence"))?.value;

    if (!name || !licence) {
      alert("Os campos são obrigatórios.");
      return;
    }

    // Verifica se a entrada corresponde ao padrão de placa
    const licensePlateRegex = /^[A-Z]{3}-\d{4}$/; // Exemplo: AAA-1234
    if (!licensePlateRegex.test(licence)) {
      alert("Placa inválida. O formato deve ser AAA-1234.");
      return;
    }

    // Verifica se o campo de nome do dono foi preenchido
    if (!this.owner) {
      alert("O campo Nome do Dono é obrigatório.");
      return;
    }

    const card = { name, licence, time: new Date(), owner: this.owner };
    const garage = this.getGarage();
    garage.push(card);
    localStorage.setItem('garage', JSON.stringify(garage));
    this.addCarToGarage(card);
    (<HTMLInputElement>document.querySelector("#name")).value = "";
    (<HTMLInputElement>document.querySelector("#licence")).value = "";
    this.owner = ""; // Limpa o campo do nome do dono
  }


  ngOnInit(): void {
    this.renderGarage();
    const sendButton = document.querySelector<HTMLButtonElement>("#send");
    if (sendButton) {
      sendButton.addEventListener("click", () => {
        this.onClickSend();
      });
    }
    const garageElement = document.querySelector<HTMLTableElement>("#garage");
    if (garageElement) {
      garageElement.addEventListener("click", (e) => {
        const target = e.target as HTMLElement;
        if (target.className === "delete") {
          const tableRow = target.parentElement?.parentElement as HTMLTableRowElement;
          if (tableRow) {
            const cells = Array.from(tableRow.cells) as HTMLTableDataCellElement[];
            this.checkOut(cells);
          }
        }
      });   
    }
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <meta http-equiv="X-UA-Compatible" content="ie=edge">
  <title>Estacionamento</title>
  <link rel="stylesheet" href="app.component.css">
</head>

<body>
  <div class="orange-stripe">
    <h1>Estacionamento</h1>
  </div>

  <div>

    <input type="text" id="name" placeholder="Nome do veículo" autocomplete="off" required>
    <input type="text" id="licence" placeholder="Placa do veículo" autocomplete="off" required>
    <button id="send" class="success" (click)="onClickSend()">Registrar</button>
  </div>

  <table class="table">
    <thead>
      <tr>
        <th>Proprietário</th>
        <th>Veículo</th>
        <th>Placa</th>
        <th>Entrada</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody id="garage">
      <tr *ngFor="let car of getGarage()">
        <td>{{ car.owner }}</td>
        <td>{{ car.name }}</td>
        <td>{{ car.licence }}</td>
        <td>{{ car.time | date:'short' }}</td>
        <td><button class="delete" (click)="checkOut(car)">x</button></td>
      </tr>
    </tbody>
  </table>

  <script src="app.component.ts"></script>
</body>

</html>
