import { Component } from '@angular/core';

interface Vehicle {
  name: string;
  license: string;
  owner: string;
  entry: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title(title: any) {
    throw new Error('Method not implemented.');
  }
  vehicles: Vehicle[] = [];
  showHistoryWindow: boolean = false;
  selectedVehicle: Vehicle | null = null;

  addVehicle(): void {
    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenseInput = document.getElementById('licence') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    const vehicle: Vehicle = {
      name: nameInput.value,
      license: licenseInput.value,
      owner: ownerInput.value,
      entry: new Date().toLocaleString()
    };

    this.vehicles.push(vehicle);

    nameInput.value = '';
    licenseInput.value = '';
    ownerInput.value = '';
  }

  removeVehicle(index: number): void {
    this.vehicles.splice(index, 1);
  }

  openHistoryWindow(): void {
    this.showHistoryWindow = true;
  }

  closeHistoryWindow(): void {
    this.showHistoryWindow = false;
  }

  selectVehicle(vehicle: Vehicle): void {
    this.selectedVehicle = vehicle;
  }

  updateVehicle(): void {
    const nameInput = document.getElementById('edit-name') as HTMLInputElement;
    const licenseInput = document.getElementById('edit-licence') as HTMLInputElement;
    const ownerInput = document.getElementById('edit-owner') as HTMLInputElement;

    if (this.selectedVehicle) {
      this.selectedVehicle.name = nameInput.value;
      this.selectedVehicle.license = licenseInput.value;
      this.selectedVehicle.owner = ownerInput.value;
    }

    this.selectedVehicle = null;
  }

  cancelUpdate(): void {
    this.selectedVehicle = null;
  }
}
