import { Component } from '@angular/core';

interface Car {
  id: number;
  vehicle: string;
  licensePlate: string;
  owner: string;
  entryTime: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'EstacionaCar';
  garage: Car[] = [];
  vehicle = '';
  licensePlate = '';
  owner = '';
  entryTime = '';

  constructor() {
    const savedGarage = localStorage.getItem('garage');
    if (savedGarage) {
      this.garage = JSON.parse(savedGarage);
    }
  }

  registerCar() {
    if (this.vehicle && this.licensePlate && this.owner) {
      const newCar: Car = {
        id: this.generateId(),
        vehicle: this.vehicle,
        licensePlate: this.licensePlate,
        owner: this.owner,
        entryTime: this.getCurrentTime()
      };
      this.garage.push(newCar);
      this.saveGarage(); // Salvar a lista atualizada no armazenamento local
      this.clearForm();
    }
  }

  generateId(): number {
    return this.garage.length > 0 ? this.garage[this.garage.length - 1].id + 1 : 1;
  }

  getCurrentTime(): string {
    const now = new Date();
    return now.toISOString();
  }

  clearForm() {
    this.vehicle = '';
    this.licensePlate = '';
    this.owner = '';
  }

  deleteCar(id: number) {
    const confirmDelete = confirm('Tem certeza de que deseja excluir este carro?');
    if (confirmDelete) {
      this.garage = this.garage.filter(car => car.id !== id);
      this.saveGarage(); // Salvar a lista atualizada no armazenamento local
    }
  }

  calculateDuration(entryTime: string): string {
    const entry = new Date(entryTime);
    const now = new Date();
    const diff = Math.abs(now.getTime() - entry.getTime());
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    return `${hours}h ${minutes}min`;
  }

  saveGarage() {
    localStorage.setItem('garage', JSON.stringify(this.garage));
  }

  showHistory() {
    const newWindow = window.open('', '_blank', 'width=600,height=400');
    if (newWindow) {
      newWindow.document.write(`
        <html>
        <head>
          <title>Histórico</title>
          <style>
            /* Estilos CSS para a tabela no histórico */
            table {
              width: 100%;
              border-collapse: collapse;
            }
            th, td {
              padding: 8px;
              border-bottom: 1px solid #ddd;
            }
            th {
              background-color: #f2f2f2;
              font-weight: bold;
            }
          </style>
        </head>
        <body>
          <h1>Histórico</h1>
          <table>
            <thead>
              <tr>
                <th>Veículo</th>
                <th>Placa</th>
                <th>Proprietário</th>
                <th>Entrada</th>
                <th>Duração</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              ${this.garage.map(car => `
                <tr>
                  <td>${car.vehicle}</td>
                  <td>${car.licensePlate}</td>
                  <td>${car.owner}</td>
                  <td>${car.entryTime}</td>
                  <td>${this.calculateDuration(car.entryTime)}</td>
                  <td>
                    <button onclick="window.opener.editCar(${car.id})">Editar</button>
                    <button onclick="window.opener.deleteCar(${car.id})">Excluir</button>
                  </td>
                </tr>
              `).join('')}
            </tbody>
          </table>
        </body>
        </html>
      `);
    }
  }

  editCar(id: number) {
    const carIndex = this.garage.findIndex(car => car.id === id);
    if (carIndex !== -1) {
      const car = this.garage[carIndex];
      this.vehicle = car.vehicle;
      this.licensePlate = car.licensePlate;
      this.owner = car.owner;
      this.garage.splice(carIndex, 1); // Remover o carro da lista principal após abrir o formulário de edição
      this.saveGarage(); // Salvar a lista atualizada no armazenamento local
    }
  }
}
