<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <meta http-equiv="X-UA-Compatible" content="ie=edge">
  <title>Estacionamento</title>
  <link rel="stylesheet" href="app.component.css">
</head>

<body>
  <div class="barra-superior">
    <img src="assets/Logo.png" alt="Logo" class="logo-img">
    <h1 class="title">EstacionaCar</h1>
    <button (click)="showHistory()" style="width: 150px; height: 50px; color: rgb(255, 255, 255); background-color: #850a0a; border-width: 3px; border-style: solid; font-weight: bold; border-color: rgb(12, 12, 12);">Exibir Histórico</button>
  </div>

  <div>
    <input type="text" [(ngModel)]="vehicle" placeholder="Nome do veículo" autocomplete="off">
    <input type="text" [(ngModel)]="licensePlate" placeholder="Placa do veículo" autocomplete="off">
    <input type="text" [(ngModel)]="owner" placeholder="Proprietário" autocomplete="off">
    <button (click)="registerCar()" class="success">Registrar</button>
  </div>

  <table class="table">
    <thead>
      <tr>
        <th>Veículo</th>
        <th>Placa</th>
        <th>Proprietário</th>
        <th>Entrada</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let car of garage">
        <td>{{ car.vehicle }}</td>
        <td>{{ car.licensePlate }}</td>
        <td>{{ car.owner }}</td>
        <td>{{ car.entryTime | date:'dd/MM/yyyy HH:mm:ss' }}</td>
        <td>
          <button (click)="editCar(car.id)">Editar</button>
          <button (click)="deleteCar(car.id)">Deletar</button>
        </td>
      </tr>
    </tbody>
  </table>

  <img class="imagem-direita" src="assets/tabela.png" alt="SDSC">
</body>

</html>
