import { Component, OnInit } from '@angular/core';
import { format } from 'date-fns';
import { Car } from '../car.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-historico',
  templateUrl: './historico.component.html',
  styleUrls: ['./historico.component.css']
})
export class HistoricoComponent implements OnInit {
  historico: Car[] = [];

  constructor(private router: Router) { }

  ngOnInit(): void {
    this.historico = this.getHistorico();
  }

  getHistorico(): Car[] {
    const garageData = localStorage.getItem('garage');
    const garage: Car[] = garageData ? JSON.parse(garageData) : [];
    return garage.filter(car => car.deleted);
  }

  formatDateTime(dateTime: Date): string {
    return format(dateTime, 'dd/MM/yyyy HH:mm');
  }
}
