import { Component, OnInit } from '@angular/core';
import { format } from 'date-fns';
import { Router } from '@angular/router';

interface Car {
  id: number;
  name: string;
  licence: string;
  time: string;
  owner: string;
  deleted?: boolean;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'EstacionaCar';
  cars: Car[] = [];
  editingCar: Car | null = null;
  nextCarId = 1;

  constructor(private router: Router) {}

  ngOnInit(): void {
    const savedCars = localStorage.getItem('cars');
    if (savedCars) {
      this.cars = JSON.parse(savedCars);
      this.nextCarId = Math.max(...this.cars.map(car => car.id)) + 1;
    }
  }

  saveCarsToStorage(): void {
    localStorage.setItem('cars', JSON.stringify(this.cars));
  }

  onClickSend(): void {
    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenceInput = document.getElementById('licence') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    const name = nameInput.value.trim();
    const licence = licenceInput.value.trim();
    const owner = ownerInput.value.trim();

    if (name && licence && owner) {
      const now = new Date();
      const time = format(now, 'HH:mm:ss');
      const car: Car = { id: this.nextCarId++, name, licence, time, owner };
      this.cars.push(car);
      this.saveCarsToStorage();

      nameInput.value = '';
      licenceInput.value = '';
      ownerInput.value = '';
    }
  }

  onDeleteCar(car: Car): void {
    car.deleted = true;
    this.saveCarsToStorage();
  }

  onEditCar(car: Car): void {
    this.editingCar = { ...car };
  }

  onSaveEdit(): void {
    if (this.editingCar) {
      const index = this.cars.findIndex(car => car.id === this.editingCar!.id);
      if (index !== -1) {
        this.cars[index] = { ...this.editingCar };
        this.saveCarsToStorage();
        this.editingCar = null;
      }
    }
  }

  onCancelEdit(): void {
    this.editingCar = null;
  }

  showHistory(): void {
    this.router.navigateByUrl('/history');
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <meta http-equiv="X-UA-Compatible" content="ie=edge">
  <title>Estacionamento</title>
  <link rel="stylesheet" href="app.component.css">
</head>
<body>
  <div class="barra-superior">
    <img src="assets/Logo.png" alt="Logo" class="logo-img">
    <h1 class="title">EstacionaCar</h1>
    <button (click)="openHistoryWindow()">Exibir Histórico</button>
  </div>

  <div>
    <input type="text" id="name" placeholder="Nome do veículo" autocomplete="off">
    <input type="text" id="license" placeholder="Placa do veículo" autocomplete="off">
    <input type="text" id="owner" placeholder="Proprietário" autocomplete="off">
    <button (click)="addVehicle()">Registrar</button>
  </div>

  <table class="table">
    <thead>
      <tr>
        <th>Veículo</th>
        <th>Placa</th>
        <th>Proprietário</th>
        <th>Entrada</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let vehicle of vehicles; let i = index">
        <td>{{ vehicle.name }}</td>
        <td>{{ vehicle.license }}</td>
        <td>{{ vehicle.owner }}</td>
        <td>{{ vehicle.entry | date:'dd/MM/yyyy HH:mm' }}</td>
        <td>
          <button (click)="removeVehicle(i)">Remover</button>
          <button (click)="editVehicle(i)">Editar</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="showHistoryWindow" class="history-window">
    <button class="close-button" (click)="closeHistoryWindow()">Fechar</button>
    <table>
      <thead>
        <tr>
          <th>Veículo</th>
          <th>Placa</th>
          <th>Proprietário</th>
          <th>Entrada</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let vehicle of vehicles">
          <td>{{ vehicle.name }}</td>
          <td>{{ vehicle.license }}</td>
          <td>{{ vehicle.owner }}</td>
          <td>{{ vehicle.entry | date:'dd/MM/yyyy HH:mm' }}</td>
        </tr>
      </tbody>
    </table>
  </div>
</body>
</html>
