import { Component } from '@angular/core';
import { Router } from '@angular/router';

interface Car {
  id: number;
  vehicle: string;
  licensePlate: string;
  owner: string;
  entryTime: string;
}

@Component({
  selector: 'app-history',
  templateUrl: './history.component.html',
  styleUrls: ['./history.component.css']
})
export class HistoryComponent {
  garage: Car[] = [];

  constructor(private router: Router) {
    const savedGarage = localStorage.getItem('garage');
    if (savedGarage) {
      this.garage = JSON.parse(savedGarage);
    }
  }

  calculateDuration(entryTime: string): string {
    const entry = new Date(entryTime);
    const now = new Date();
    const diff = Math.abs(now.getTime() - entry.getTime());
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
    return `${hours}h ${minutes}min`;
  }

  goBack() {
    this.router.navigate(['/']);
  }

  editCar(id: number) {
    // Implemente a lógica de edição aqui
  }

  deleteCar(id: number) {
    // Implemente a lógica de exclusão aqui
  }
}
<h1>Histórico</h1>

<button (click)="goBack()">Voltar</button>

<table>
  <thead>
    <tr>
      <th>Veículo</th>
      <th>Placa</th>
      <th>Proprietário</th>
      <th>Entrada</th>
      <th>Duração</th>
      <th>Ações</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let car of garage">
      <td>{{ car.vehicle }}</td>
      <td>{{ car.licensePlate }}</td>
      <td>{{ car.owner }}</td>
      <td>{{ car.entryTime }}</td>
      <td>{{ calculateDuration(car.entryTime) }}</td>
      <td>
        <button (click)="editCar(car.id)">Editar</button>
        <button (click)="deleteCar(car.id)">Excluir</button>
      </td>
    </tr>
  </tbody>
</table>
