import { Component, OnInit } from '@angular/core';
import { Car } from '../app.component';

@Component({
  selector: 'app-historico',
  templateUrl: './historico.component.html',
  styleUrls: ['./historico.component.css']
})
export class HistoricoComponent implements OnInit {
  historico: Car[] = [];

  constructor() { }

  ngOnInit(): void {
    this.historico = this.getHistorico();
  }

  getHistorico(): Car[] {
    const garageData = localStorage.getItem('garage');
    return garageData ? JSON.parse(garageData).filter((car: Car) => car.deleted) : [];
  }
}
<h2>Histórico de Veículos</h2>

<table>
  <thead>
    <tr>
      <th>Veículo</th>
      <th>Placa</th>
      <th>Proprietário</th>
      <th>Entrada</th>
      <th>Ações</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let car of historico">
      <td>{{ car.name }}</td>
      <td>{{ car.licence }}</td>
      <td>{{ car.owner }}</td>
      <td>{{ formatDateTime(car.time) }}</td>
      <td><button class="delete" (click)="checkout(car)">Deletar</button></td>
    </tr>
  </tbody>
</table>
