import { Component } from '@angular/core';

interface Vehicle {
  name: string;
  license: string;
  owner: string;
  entry: Date;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title(title: any) {
    throw new Error('Method not implemented.');
  }
  vehicles: Vehicle[] = [];
  showHistoryWindow = false;
  editedVehicleIndex: number | null = null;

  addVehicle(): void {
    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenseInput = document.getElementById('license') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    const name = nameInput.value;
    const license = licenseInput.value;
    const owner = ownerInput.value;
    const entry = new Date();

    if (name && license && owner) {
      const vehicle: Vehicle = { name, license, owner, entry };
      this.vehicles.push(vehicle);
      this.clearInputs();
    }
  }

  removeVehicle(index: number): void {
    if (confirm('Tem certeza que deseja remover este veículo?')) {
      this.vehicles.splice(index, 1);
    }
  }

  editVehicle(index: number): void {
    this.editedVehicleIndex = index;
    const vehicle = this.vehicles[index];

    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenseInput = document.getElementById('license') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    nameInput.value = vehicle.name;
    licenseInput.value = vehicle.license;
    ownerInput.value = vehicle.owner;
  }

  saveEditedVehicle(): void {
    if (this.editedVehicleIndex !== null) {
      const nameInput = document.getElementById('name') as HTMLInputElement;
      const licenseInput = document.getElementById('license') as HTMLInputElement;
      const ownerInput = document.getElementById('owner') as HTMLInputElement;

      const name = nameInput.value;
      const license = licenseInput.value;
      const owner = ownerInput.value;

      if (name && license && owner) {
        const vehicle = this.vehicles[this.editedVehicleIndex];
        vehicle.name = name;
        vehicle.license = license;
        vehicle.owner = owner;
        this.editedVehicleIndex = null;
        this.clearInputs();
      }
    }
  }

  clearInputs(): void {
    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenseInput = document.getElementById('license') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    nameInput.value = '';
    licenseInput.value = '';
    ownerInput.value = '';
  }

  openHistoryWindow(): void {
    this.showHistoryWindow = true;
  }

  closeHistoryWindow(): void {
    this.showHistoryWindow = false;
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <meta http-equiv="X-UA-Compatible" content="ie=edge">
  <title>Estacionamento</title>
  <link rel="stylesheet" href="app.component.css">
</head>
<body>
  <div class="barra-superior">
    <img src="assets/Logo.png" alt="Logo" class="logo-img">
    <h1 class="title">EstacionaCar</h1>
    <button (click)="openHistoryWindow()">Exibir Histórico</button>
  </div>

  <div>
    <input type="text" id="name" placeholder="Nome do veículo" autocomplete="off">
    <input type="text" id="license" placeholder="Placa do veículo" autocomplete="off">
    <input type="text" id="owner" placeholder="Proprietário" autocomplete="off">
    <button (click)="addVehicle()">Registrar</button>
  </div>

  <table class="table">
    <thead>
      <tr>
        <th>Veículo</th>
        <th>Placa</th>
        <th>Proprietário</th>
        <th>Entrada</th>
        <th>Ações</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let vehicle of vehicles; let i = index">
        <td>{{ vehicle.name }}</td>
        <td>{{ vehicle.license }}</td>
        <td>{{ vehicle.owner }}</td>
        <td>{{ vehicle.entry | date:'dd/MM/yyyy HH:mm' }}</td>
        <td>
          <button (click)="removeVehicle(i)">Remover</button>
          <button (click)="editVehicle(i)">Editar</button>
        </td>
      </tr>
    </tbody>
  </table>

  <div *ngIf="showHistoryWindow" class="history-window">
    <button class="close-button" (click)="closeHistoryWindow()">Fechar</button>
    <table>
      <thead>
        <tr>
          <th>Veículo</th>
          <th>Placa</th>
          <th>Proprietário</th>
          <th>Entrada</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let vehicle of vehicles">
          <td>{{ vehicle.name }}</td>
          <td>{{ vehicle.license }}</td>
          <td>{{ vehicle.owner }}</td>
          <td>{{ vehicle.entry | date:'dd/MM/yyyy HH:mm' }}</td>
        </tr>
      </tbody>
    </table>
  </div>
</body>
</html>
