    <title>Estacionamento</title>
    <link rel="stylesheet" href="app.component.css">  
</head>
<body>
    <div>
        <input type="text" id="name" placeholder="Nome do veículo" autocomplete="off">
        <input type="text" id="licence" placeholder="Placa do veículo" autocomplete="off">
        <input type="text" id="owner" placeholder="Proprietário" autocomplete="off">
        <button id="send" class="success">Registrar</button>

    </div>
    <table class="table">
        <thead>
            <tr>
                <th>Veículo</th>
                <th>Placa</th>
                <th>Proprietário</th>
                <th>Entrada</th>
                <th>Ações</th>
            </tr>
        </thead>
        <tbody id="garage"></tbody>
    </table>
</body>

<script src="app.component.ts"></script>
</html>

