import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Car } from './car.model';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title(title: any) {
    throw new Error('Method not implemented.');
  }
  cars: Car[] = [];
  deletedCars: Car[] = [];

  constructor(private router: Router) {}

  showHistory(): void {
    this.router.navigate(['/historico']);
  }

  registerCar(): void {
    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenseInput = document.getElementById('license') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    const name = nameInput.value;
    const licensePlate = licenseInput.value;
    const owner = ownerInput.value;

    if (name && licensePlate && owner) {
      const car: Car = {
        name,
        licensePlate,
        owner,
        time: new Date()
      };

      this.cars.push(car);
      nameInput.value = '';
      licenseInput.value = '';
      ownerInput.value = '';
    }
  }

  deleteCar(index: number): void {
    const car = this.cars.splice(index, 1)[0];
    this.deletedCars.push(car);
  }
}
