import { Component, OnInit } from '@angular/core';
import { format } from 'date-fns';
import { Router } from '@angular/router';


interface Car {
  id: number;
  name: string;
  licence: string;
  time: string;
  owner: string;
  deleted?: boolean;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'EstacionaCar';
  cars: Car[] = [];
  editingCar: Car | null = null;
  nextCarId = 1;
  garage: any;

  constructor(private router: Router) {}

  ngOnInit(): void {
    const savedCars = localStorage.getItem('cars');
    if (savedCars) {
      this.cars = JSON.parse(savedCars);
      this.nextCarId = Math.max(...this.cars.map(car => car.id)) + 1;
    }
  }

  saveCarsToStorage(): void {
    localStorage.setItem('cars', JSON.stringify(this.cars));
  }

  onClickSend(): void {
    const nameInput = document.getElementById('name') as HTMLInputElement;
    const licenceInput = document.getElementById('licence') as HTMLInputElement;
    const ownerInput = document.getElementById('owner') as HTMLInputElement;

    const name = nameInput.value.trim();
    const licence = licenceInput.value.trim();
    const owner = ownerInput.value.trim();

    if (name && licence && owner) {
      const now = new Date();
      const time = format(now, 'HH:mm:ss');
      const car: Car = { id: this.nextCarId++, name, licence, time, owner };
      this.cars.push(car);
      this.saveCarsToStorage();

      nameInput.value = '';
      licenceInput.value = '';
      ownerInput.value = '';
    }
  }

  onDeleteCar(car: Car): void {
    car.deleted = true;
    this.saveCarsToStorage();
  }
  deleteCar(id: number) {
    const confirmDelete = confirm('Tem certeza de que deseja excluir este carro?');
    if (confirmDelete) {
      this.garage = this.garage.filter(car => car.id !== id);
      this.saveGarage(); // Salvar a lista atualizada no armazenamento local
    }
  }
  saveGarage() {
    throw new Error('Method not implemented.');
  }

  onEditCar(car: Car): void {
    this.editingCar = { ...car };
  }

  onSaveEdit(): void {
    if (this.editingCar) {
      const index = this.cars.findIndex(car => car.id === this.editingCar!.id);
      if (index !== -1) {
        this.cars[index] = { ...this.editingCar };
        this.saveCarsToStorage();
        this.editingCar = null;
      }
    }
  }

  onCancelEdit(): void {
    this.editingCar = null;
  }

  showHistory() {
    const newWindow = window.open('', '_blank', 'width=600,height=400');
    if (newWindow) {
      newWindow.document.write(`
        <html>
        <head>
          <title>Histórico</title>
          <style>
            /* Estilos CSS para a tabela no histórico */
            table {
              width: 100%;
              border-collapse: collapse;
            }
            th, td {
              padding: 8px;
              border-bottom: 1px solid #ddd;
            }
            th {
              background-color: #f2f2f2;
              font-weight: bold;
            }
          </style>
        </head>
        <body>
          <h1>Histórico</h1>
          <table>
            <thead>
              <tr>
                <th>Veículo</th>
                <th>Placa</th>
                <th>Proprietário</th>
                <th>Entrada</th>
                <th>Permanência</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              ${this.garage
                .map(
                  car => `
                  <tr>
                    <td>${car.vehicle}</td>
                    <td>${car.licensePlate}</td>
                    <td>${car.owner}</td>
                    <td>${car.entryTime}</td>
                    <td>${this.calculateDuration(car.entryTime)}</td>
                    <td>
                      <button onclick="window.opener.editCar(${car.id})">Editar</button>
                      <button onclick="window.opener.deleteCar(${car.id})">Deletar</button>
                    </td>
                  </tr>
                `
                )
                .join('')}
            </tbody>
          </table>
        </body>
        </html>
      `);
    } else {
      alert('O bloqueador de pop-ups está ativado. Por favor, desative-o para exibir o histórico.');
    }
  }
