import { Component, OnInit } from '@angular/core';
import { format } from 'date-fns';
import { Router } from '@angular/router';

interface Car {
  name: string;
  licence: string;
  time: string;
  owner: string;
  deleted?: boolean;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'estacionamento';
  garage: Car[] = [];

  constructor(private router: Router) {}

  ngOnInit(): void {
    const ownerInput = <HTMLInputElement>document.querySelector("#owner");
    ownerInput.value = localStorage.getItem('owner') || '';
    ownerInput.addEventListener('input', () => {
      localStorage.setItem('owner', ownerInput.value);
    });

    this.renderGarage();
    const sendButton = document.querySelector<HTMLButtonElement>("#send");
    if (sendButton) {
      sendButton.addEventListener("click", () => {
        this.onClickSend();
      });
    }
  }

  renderGarage(): void {
    const garageData = localStorage.getItem('garage');
    if (garageData) {
      this.garage = JSON.parse(garageData);
    }
  }

  formatDateTime(dateTime: string): string {
    return format(new Date(dateTime), 'dd/MM/yyyy HH:mm');
  }

  checkOut(car: Car): void {
    const startTime = new Date(car.time);
    const endTime = new Date();
    const period = endTime.getTime() - startTime.getTime();
    const formattedPeriod = this.convertPeriod(period);
    const msg = `O veículo ${car.name} de placa ${car.licence} permaneceu ${formattedPeriod} estacionado.\n\nDeseja encerrar?`;
    if (!confirm(msg)) {
      return;
    }
    this.garage = this.garage.map(c => {
      if (c.licence === car.licence) {
        return {
          ...c,
          deleted: true // Marcar o carro como deletado
        };
      }
      return c;
    });
    localStorage.setItem('garage', JSON.stringify(this.garage));
  }

  onClickSend(): void {
    const nameInput = <HTMLInputElement>document.querySelector("#name");
    const licenceInput = <HTMLInputElement>document.querySelector("#licence");
    const ownerInput = <HTMLInputElement>document.querySelector("#owner");

    const name = nameInput?.value;
    const licence = licenceInput?.value;
    const owner = ownerInput?.value;

    if (!name || !licence) {
      alert("Os campos são obrigatórios.");
      return;
    }

    const licensePlateRegex = /^[A-Z]{3}-\d{4}$/; // Exemplo: AAA-1234
    if (!licensePlateRegex.test(licence)) {
      alert("Placa inválida. O formato deve ser AAA-1234.");
      return;
    }

    if (!owner) {
      alert("O campo Proprietário é obrigatório.");
      return;
    }

    const time = new Date().toISOString();
    const car: Car = { name, licence, time, owner };
    this.garage.push(car);
    localStorage.setItem('garage', JSON.stringify(this.garage));

    nameInput.value = "";
    licenceInput.value = "";
    ownerInput.value = "";
  }

  convertPeriod(milliseconds: number): string {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;
    return `${hours}h ${minutes}min ${seconds}s`;
  }

  showHistory(): void {
    this.router.navigate(['/historico']);
  }
}
