    <h1>Estacionamento</h1>   
    <button (click)="showHistory()" style="width: 100px; height: 50px; floa: left; margin-left: 10px;">Mostrar Histórico</button> 
</div>


<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <meta http-equiv="X-UA-Compatible" content="ie=edge">
    <title>Estacionamento</title>
    <link rel="stylesheet" href="app.component.css">  
</head>
<body>
    <div>
        <input type="text" id="name" placeholder="Nome do veículo" autocomplete="off">
        <input type="text" id="licence" placeholder="Placa do veículo" autocomplete="off">
        <input type="text" id="owner" placeholder="Proprietário" autocomplete="off">
        <button id="send" class="success">Registrar</button>

    </div>
    <table class="table">
        <thead>
            <tr>
                <th>Veículo</th>
                <th>Placa</th>
                <th>Proprietário</th>
                <th>Entrada</th>
                <th>Ações</th>
            </tr>
        </thead>
        <tbody id="garage"></tbody>
    </table>
</body>

<script src="app.component.ts"></script>
</html>

